import { createAsyncThunk, createSlice } from '@reduxjs/toolkit';


const getTodoAsync = createAsyncThunk("todo/getTodoAsync" , async () => {
    const response = await fetch('https://jsonplaceholder.typicode.com/todos');
    // if (response.ok) {
        const todo = await response.json()
        console.log(todo)
    // }
} )

getTodoAsync()


let id=100;
const todoSlice = createSlice({
    name:"todo",
    initialState:[
		{ id: 1, title: 'todo1', completed: false },
		{ id: 2, title: 'todo2', completed: false },
		{ id: 3, title: 'todo3', completed: false },
		{ id: 4, title: 'todo4', completed: false },
		{ id: 5, title: 'todo5', completed: false },
	],
    reducers:{
        addTask:(state,action) =>{
            return [
                ...state, {
                    id:id++,
                    title:action.payload.title ,
                    completed:false
                }
            ]
        },
        removeTask : (state,action) =>{
            return state.filter(task => task.id !== action.payload.id)
        },
        taskCompleted : (state,action) =>{
            return state.map(task => task.id === action.payload.id ? {...task, completed: !task.completed } : task )
        },
    }
})

export const { addTask , removeTask, taskCompleted} = todoSlice.actions ;

export default todoSlice.reducer ;
